mapper.update:
update diaryDB
set title = #{title}, content = #{content}, thumbnail = #{thumbnail}
where iboard = #{iboard}

mapper.select:
select A.iuser, A.title, A.content, date_format(A.regdt, '%Y-%m-%d') as regdt, B.profileImg, B.nickname, B.introduction, (
    select count(*)
    from favDB
    where iboard = #{iboard} and iuser = #{iuser}
) as isFav
from diaryDB A
inner join userDB B
on A.iuser = B.iuser
where A.iboard = #{iboard}

mapper.update:
update cmtDB
set comment = #{comment}
where icmt = #{icmt} and iuser = #{iuser}
